let skills=[{name:"figma",weight:.1,has:!1},{name:"wireframing",weight:.06,has:!1},{name:"prototyping",weight:.08,has:!1},{name:"usability_testing",weight:.08,has:!1},{name:"heuristic_evaluation",weight:.05,has:!1},{name:"user_interviews",weight:.07,has:!1},{name:"accessibility",weight:.06,has:!1},{name:"html_css",weight:.05,has:!1},{name:"javascript",weight:.04,has:!1},{name:"python",weight:.04,has:!1},{name:"sql",weight:.03,has:!1},{name:"ab_testing",weight:.04,has:!1},{name:"data_visualization",weight:.03,has:!1}],sum=e=>e.reduce((e,t)=>e+t,0);let chart;function renderSkills(){const e=document.getElementById("skills");e.innerHTML="",skills.forEach((t,a)=>{const n=document.createElement("div");n.innerHTML=`<label><input type="checkbox" data-idx="${a}"/> ${t.name} (${t.weight})</label>`,e.appendChild(n)})}function compute(){document.querySelectorAll("#skills input").forEach(e=>skills[e.dataset.idx].has=e.checked);const e=skills.filter(e=>e.has),t=e.length/skills.length,a=sum(e.map(e=>e.weight))/sum(skills.map(e=>e.weight));document.getElementById("rawPct").innerText=(100*t).toFixed(1)+"%",document.getElementById("wPct").innerText=(100*a).toFixed(1)+"%";const n=sum(e.map(e=>e.weight)),s=sum(skills.map(e=>e.weight))-n;chart?((chart.data.datasets[0].data=[n,s]),chart.update()):chart=new Chart(document.getElementById("chart"),{type:"doughnut",data:{labels:["Covered","Uncovered"],datasets:[{data:[n,s]}]},options:{plugins:{legend:{labels:{color:"#e5e7eb"}}}}});const i=skills.filter(e=>!e.has).sort((e,t)=>t.weight-e.weight).slice(0,5);document.getElementById("missingList").innerText="Top missing: "+i.map(e=>e.name).join(", ")}document.getElementById("compute").onclick=compute,renderSkills();
